/**
 * Gets the day of year (1-365/366)
 */
export function getDayOfYear(date: Date): number {
  const start = new Date(date.getFullYear(), 0, 0);
  const diff = date.getTime() - start.getTime();
  return Math.floor(diff / (1000 * 60 * 60 * 24));
}

/**
 * Gets a specific admin verse by day of year
 */
export function getAdminVerse(dayOfYear: number): AdminVerse {
  const verseIndex = (dayOfYear - 1) % ADMIN_VERSES.length;
  return ADMIN_VERSES[verseIndex];
}

/**
 * Gets today's admin verse
 */
export function getTodaysAdminVerse(): AdminVerse {
  const today = new Date();
  const dayOfYear = getDayOfYear(today);
  return getAdminVerse(dayOfYear);
}