// Para completar 365 versículos precisamos apenas referenciar pelo dia do ano
export function getDailyVerse(dayOfYear: number): DailyVerse {
  // Garante que o índice está dentro do range (0-364)
  const verseIndex = (dayOfYear - 1) % DAILY_VERSES.length;
  return DAILY_VERSES[verseIndex];
}

// Função para calcular o dia do ano (1-365/366)
export function getDayOfYear(date: Date): number {
  const start = new Date(date.getFullYear(), 0, 0);
  const diff = date.getTime() - start.getTime();
  const oneDay = 1000 * 60 * 60 * 24;
  return Math.floor(diff / oneDay);
}